    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
     
        String ram = jRam.getText();
      String modelo = Jmodelo.getText();
        if ("8gb" .equals(ram)&& "i3".equals(modelo)  ) {
            new Thread () {
                public void run () {
                    for ( int i = 5; 1 < 70;i++){
                        try {
                            sleep(100);
                            jBarra1.setValue(80);
                            
                            
                            jBarra2.setValue(70);
                            
                            jBarra3.setValue(97);
                        } catch(InterruptedException ex){
                            
                        }
                    }
                }
            }.start();
        }else  {if ("12gb" .equals(ram)&& "i5".equals(modelo)  ) {
            new Thread () {
                public void run () {
                    for ( int i = 5; 1 < 70;i++){
                        try {
                            sleep(100);
                            jBarra1.setValue(60);
                            
                            
                            jBarra2.setValue(50);
                            
                            jBarra3.setValue(40);
                        } catch(InterruptedException ex){
                            
                        }
                    }
                }
            }.start();
        }  
            
            
            
        }
    }//GEN-LAST:event_jButton1ActionPerformed

